create table users (
	id INT AUTO_INCREMENT PRIMARY KEY,
	full_name VARCHAR(50),
	email VARCHAR(50),
	date_added DATE
);

create table posts (
	id INT AUTO_INCREMENT PRIMARY KEY,
	description TEXT,
	user_id INT,
	date_added DATE,
	last_updated DATE
);

create table comments (
	id INT AUTO_INCREMENT PRIMARY KEY,
	description TEXT,
	post_id INT,
	user_id INT,
	date_added DATE,
	last_updated DATE
);
